mapper.select:
SELECT id, open_id, name,wx_name, cover, balance, addr_id, is_enabled enabled, create_date, login_date
from users_tb
where name=#{user.name}

mapper.update:
UPDATE users_tb
    SET
        open_id=#{user.openId},
        wx_name=#{user.wxName},
        cover=#{user.cover}
WHERE id = #{id}
